package JavaConcepts;
public class MathClass {
public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println(Math.PI);

System.out.println(Math.addExact(1, 345));
System.out.println(Math.max(6, 8));
System.out.println(Math.subtractExact(45, 56));
System.out.println(Math.multiplyExact(56, 2));
System.out.println(Math.floorDiv(2, 5));
System.out.println(Math.incrementExact(5));
System.out.println(Math.sqrt(100));

for(int i=0;i<=5;i++) {
System.out.println(Math.random());
	}
	}
}
