package JavaConcepts;

public class PrintLnEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    
		for(int i=0;i<5;i++)
		{
			System.out.println(i); //Println will move cursor to next line
		}
		
		for(int k=0;k<10;k++)
		{
			System.out.print(k);// print will show output in horizontal manner. cursor will not move to next line
		}
	}

}
